//use tailwind styles


import {useState} from "react";

function Form(props) {
    const [state, setState] = useState(props.object);
    return (
        <div>
            {Object.keys(state).map((key) => {
                return (
                    <div className="flex flex-col border p-1 {key === 'players' ? bg-amber-100 : 'bg-white'}"  key={key}>
                        <label className="text-xl">{key}</label>
                        { key === 'players' ? (<div>
                            <button onClick={(e) => {
                                e.preventDefault()
                                setState({...state, [key]: [...state[key], {...props.object.defaults, session: 'ВАША СЕССИЯ'}]});
                                props.addNewPlayer()
                            }}>+</button>
                            <button onClick={(e) => {
                                e.preventDefault()
                                setState({...state, [key]: [...state[key], {...props.object.defaults, session: 'ВАША СЕССИЯ'}]});
                                props.addNewPlayer()
                            }}>-</button></div>): null }
                        {typeof state[key] === 'object' && state[key] !== null ? (
                            <Form object={state[key]}/>
                        ) : (
                            state[key] === true || state[key] === false ? (
                                <input
                                    className="border-2 border-black rounded-md w-5"
                                    type="checkbox"
                                    checked={state[key]}
                                    onChange={(e) => {
                                        setState({...state, [key]: e.target.checked});
                                        props.object[key] = e.target.checked;
                                    }}
                                />
                            ) : (
                            <input
                                className="border-2 border-black rounded-md"
                                type="text"
                                value={state[key]}
                                onChange={(e) => {
                                    const value = e.target.value;
                                    setState({...state, [key]: value});
                                    try {
                                        props.object[key] = JSON.parse(value);
                                    } catch (e) {
                                        props.object[key] = value;
                                    }
                                }}
                            />
                            ))}
                    </div>
                );
            })}
        </div>
    );
}

export default Form;
